import {
  PerspectiveCamera,
  Scene,
  WebGLRenderer,
  MeshStandardMaterial,
  Mesh,
  Color,
  SphereBufferGeometry,
  HemisphereLight,
  DirectionalLight,
  DirectionalLightHelper,
  ShaderMaterial,
} from "three";
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls";
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader.js';
import { promisifyLoader } from './helpers.js';

import fragmentShader from "./shaders/fragment.glsl";
import vertexShader from "./shaders/vertex.glsl";

const modelFolderNames = ['house', 'island', 'knowledge', 'ship', 'tree'];
const modelPath = './models/';
const GLTFPromiseLoader = promisifyLoader( new GLTFLoader() );

let container, scene, camera, renderer, controls;


function init() {
  container = document.querySelector(".container");
  scene = new Scene();
  scene.background = new Color("skyblue");
  loadGLTFs();
  createCamera();
  createLights();
  createRenderer();
  createGeometries();
  createControls();

  renderer.setAnimationLoop(() => {
    update();
    render();
  });
}

function loadGLTFs() {
  modelFolderNames.forEach(folderName => {
    let filePath = `${modelPath}${folderName}/${folderName}.glb`;

    let loader = new GLTFLoader();
    loader.load( filePath, function ( data ) {
      gltf = data;
      let object = gltf.scene;
      console.info( 'Load time: ' + ( performance.now() - loadStartTime ).toFixed( 2 ) + ' ms.' );

      // if ( sceneInfo.cameraPos ) {
      //   camera.position.copy( sceneInfo.cameraPos );
      // }

      // if ( sceneInfo.center ) {
      //   orbitControls.target.copy( sceneInfo.center );
      // }

      if ( sceneInfo.objectPosition ) {
        object.position.copy( sceneInfo.objectPosition );
        if ( spot1 ) {
          spot1.target.position.copy( sceneInfo.objectPosition );
        }
      }

      if ( sceneInfo.objectRotation ) {
        object.rotation.copy( sceneInfo.objectRotation );
      }

      if ( sceneInfo.objectScale ) {
        object.scale.copy( sceneInfo.objectScale );
      }

      if ( sceneInfo.addEnvMap ) {
        object.traverse( function ( node ) {
          if ( node.material && ( node.material.isMeshStandardMaterial ||
             ( node.material.isShaderMaterial && node.material.envMap !== undefined ) ) ) {
            node.material.envMap = envMap;
            node.material.envMapIntensity = 1.5; // boombox seems too dark otherwise
          }
        });
        scene.background = background;
      }

      object.traverse( function ( node ) {
        if ( node.isMesh || node.isLight ) node.castShadow = true;
      } );

      let animations = gltf.animations;

      if ( animations && animations.length ) {

        mixer = new THREE.AnimationMixer( object );

        for ( let i = 0; i < animations.length; i ++ ) {

          let animation = animations[ i ];

          // There's .3333 seconds junk at the tail of the Monster animation that
          // keeps it from looping cleanly. Clip it at 3 seconds
          if ( sceneInfo.animationTime ) {

            animation.duration = sceneInfo.animationTime;

          }

          let action = mixer.clipAction( animation );

          if ( state.playAnimation ) action.play();
        }
      }
      scene.add( object );
    }, undefined, function ( error ) {
      console.error( error );
    } );      
    console.log(filePath);
  });
  //   GLTFPromiseLoader.load( filePath )
  //     .then((loadedObject) => scene.add( loadedObject.scene ))
  //     .catch( (err) => { console.error( err ) } );
  // })
}

function createCamera() {
  const aspect = container.clientWidth / container.clientHeight;
  camera = new PerspectiveCamera(35, aspect, 0.1, 1000);
  camera.position.set(2, 1, 5);
}

function createLights() {
  const directionalLight = new DirectionalLight(0xffffff, 5);
  directionalLight.position.set(5, 5, 10);

  const directionalLightHelper = new DirectionalLightHelper(
    directionalLight,
    5
  );

  const hemisphereLight = new HemisphereLight(0xddeeff, 0x202020, 3);
  scene.add(directionalLight, directionalLightHelper, hemisphereLight);
}

function createRenderer() {
  renderer = new WebGLRenderer({ antialias: true });
  renderer.setSize(container.clientWidth, container.clientHeight);
  renderer.setPixelRatio(window.devicePixelRatio);
  renderer.physicallyCorrectLights = true;

  container.appendChild(renderer.domElement);
}

function createMaterials() {
  //const material = new MeshStandardMaterial({ wireframe: true });
  const material = new ShaderMaterial({
    fragmentShader: fragmentShader,
    vertexShader: vertexShader
  });

  return material;
}

function createGeometries() {
  const geometry = new SphereBufferGeometry(1, 30, 30);
  const material = createMaterials();

  const mesh = new Mesh(geometry, material);
  scene.add(mesh);
}

function createControls() {
  controls = new OrbitControls(camera, renderer.domElement);
}

function update() {}

function render() {
  renderer.render(scene, camera);
}

init();

function onWindowResize() {
  camera.aspect = container.clientWidth / container.clientHeight;
  camera.updateProjectionMatrix();
  renderer.setSize(container.clientWidth, container.clientHeight);
}
window.addEventListener("resize", onWindowResize, false);
